import React, { useState } from 'react'

export default function TextForm(props) {
    const handleUpperClick = () => {
        // console.log("Uppercase was clicked" + Text);
        let upperText = Text.toUpperCase();
        setText(upperText);
    }
    const handlelowercaseClick = () => {
        // console.log("Uppercase was clicked" + Text);
        let lowerText = Text.toLowerCase();
        setText(lowerText);
    }
    const handleclearClick = () => {
        // console.log("Uppercase was clicked" + Text);
        let clearText = '';
        setText(clearText);
    }

    const handleOnChange = (event) => {
        // console.log("On Change");
        setText(event.target.value);
    }

    const handelcopy = () => {
        let text = document.getElementById("myBox");
        text.select();
        navigator.clipboard.writeText(text.value)
    }
    // this is for remove extra spaces
    const removeExtraSpaces = () => {
        let newText = Text.split(/[ ]+/);
        setText(newText.join(" "))
    }
    const [Text, setText] = useState('Enter Text Here');
    return (
        <>
            <div className='container' style={{color : props.mode === 'dark'?'grey':'white'}}>
                <h1>{props.heading}</h1>
                <div className="my-3">
                    <textarea className="form-control" value={Text} onChange={handleOnChange} style ={{backgroundColor : props.mode === 'dark'?'grey':'white'}} id="myBox" rows="5"></textarea>
                </div>
                <button className="btn btn-info" onClick={handleUpperClick}>Convert to Uppercase</button>
                <button className="btn btn-primary mx-1" onClick={handlelowercaseClick}>Convert to Lowercase</button>
                <button className="btn btn-danger mx-1" onClick={handleclearClick}>Clear Text</button>
                <button className="btn btn-danger mx-1" onClick={handelcopy}>Copy Text</button>
                <button className="btn btn-danger mx-1" onClick={removeExtraSpaces}>Remove Spaces</button>



            </div>
            <div className="container my-3">
                <br />
                <h1>Text Summary</h1>
                <p>{Text.split(" ").length} words and {Text.length} characters </p>
                <p>{0.008 * Text.split(" ").length} Minutes Read </p>
                <h2>Preview</h2>
                <p>{Text}</p>

            </div>
        </>
    )
}
